// n8n Code (v2) ‚Äî filtra queries com extracted_value > 30 a partir do Trends1
const top = ($node["Trends1"].json?.related_queries?.top) ?? [];

const filtered = top
  .filter(it => Number(it?.extracted_value) > 30)
  .map(it => ({
    query: String(it?.query ?? "").trim(),
    extracted_value: Number(it?.extracted_value ?? 0)
  }));

// monta CSV e tamb√©m devolve estrutura completa se quiser reaproveitar
const resultString = filtered.map(it => it.query).join(', ');

return [{
  json: {
    filtered,
    result: resultString,
    count: filtered.length,
    // opcional: o TOP absoluto pelo maior score
    top_query: filtered.sort((a,b) => b.extracted_value - a.extracted_value)[0]?.query ?? null,
    top_score: filtered.sort((a,b) => b.extracted_value - a.extracted_value)[0]?.extracted_value ?? null
  }
}];

// n8n Code (v2) ‚Äî agrega todos os "q" do node anterior

// Pega todos os itens de entrada
const items = $input.all();

// Fun√ß√£o de normaliza√ß√£o (lowercase, sem acentos, trim, espa√ßos √∫nicos)
const normalize = (s) => (s ?? "")
  .toString()
  .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
  .toLowerCase()
  .replace(/\s+/g, ' ')
  .trim();

// Coleta todos os "q" existentes
const qAll = [];
for (const it of items) {
  // Pode vir em it.json.q
  if (it?.json?.q != null) qAll.push(String(it.json.q));

  // (Opcional) Se o item for um objeto grande e tiver um array dentro, varra
  // Ajuste se voc√™ realmente precisar:
  // if (Array.isArray(it.json?.rows)) {
  //   for (const row of it.json.rows) {
  //     if (row?.q != null) qAll.push(String(row.q));
  //   }
  // }
}

// Normaliza e remove vazios
const qNorm = qAll.map(normalize).filter(Boolean);

// Remove duplicados preservando a primeira ocorr√™ncia
const qUnique = Array.from(new Set(qNorm));

// Tamb√©m mantenha a lista original (sem normalizar) correspondente aos √∫nicos
// (caso queira exibir exatamente como veio o primeiro)
const seen = new Set();
const qUniqueOriginal = [];
for (const raw of qAll) {
  const n = normalize(raw);
  if (!n) continue;
  if (!seen.has(n)) {
    seen.add(n);
    qUniqueOriginal.push(raw);
  }
}

// Sa√≠das √∫teis
const qCsv = qUnique.join(', ');
const uniqueCount = qUnique.length;

return [
  {
    json: {
      // Todos "q" como vieram
      q_all_raw: qAll,
      // √önicos normalizados (bom para usar diretamente)
      q_unique: qUnique,
      // √önicos como vieram originalmente (caso queira preservar mai√∫sculas/acentos do primeiro)
      q_unique_original: qUniqueOriginal,
      // √önicos em CSV
      q_csv: qCsv,
      q_unique_count: uniqueCount
    }
  }
];
